mapper.insert:
INSERT INTO t_dm_user
SET idm = #{idm}
, iuser = #{iuser}

mapper.delete:
delete
from t_dm_msg
where idm = #{idm} and seq = #{seq} and iuser = #{iuser}

mapper.update:
UPDATE t_dm
SET last_msg = #{msg}
WHERE idm = #{idm}

mapper.select:
SELECT B.iuser, B.pic, B.firebase_token AS firebaseToken
FROM t_dm_user A
INNER JOIN t_user B
ON A.iuser = B.iuser
WHERE A.idm = #{idm}
AND A.iuser != #{loginedIuser}
