mapper.insert:
insert into t_user_follow
set from_iuser = #{fromIuser},
to_iuser = #{toIuser}

mapper.select:
SELECT iuser, uid, upw, nm, pic, firebase_token AS firebaseToken, created_at AS createdAt, updated_at AS updatedAt
from t_user


        uid = #{uid}


        and iuser = #{iuser}

mapper.update:
UPDATE t_user
SET firebase_token = #{firebaseToken}
WHERE iuser = #{iuser}

UPDATE t_user
SET pic = #{pic}
WHERE iuser = #{iuser}
